import base64

from docx import Document
from docx.shared import Inches
from svglib.svglib import svg2rlg
from reportlab.graphics import renderPDF, renderPM



def svg2pngtoword(svgFilepath,pngFilepath,wordFilepath):
    document = Document()
    document.add_heading('Svg Image Inserted', 0)
    p=document.add_paragraph()
    r=p.add_run()
    drawing = svg2rlg(svgFilepath)
    renderPM.drawToFile(drawing,pngFilepath)
    r.add_picture(pngFilepath)
    document.save(wordFilepath)

if __name__=='__main__':
    svgbase64="data:image/svg+xml;base64,"
    svgdecode=svgbase64.split(',')
    decoded =base64.b64decode(svgdecode[1])
    with open("gs.svg", "wb") as f: f.write(decoded)
    svg2pngtoword("gs.svg","gs.png","gs.docx")